static char *
print_byte(c)
char c;
{
static char bt[9];

    bt[0] = ( (c & 0x01) ? '1' : '0');
    bt[1] = ( (c & 0x02) ? '1' : '0');
    bt[2] = ( (c & 0x03) ? '1' : '0');
    bt[3] = ( (c & 0x04) ? '1' : '0');
    bt[4] = ( (c & 0x05) ? '1' : '0');
    bt[5] = ( (c & 0x06) ? '1' : '0');
    bt[6] = ( (c & 0x07) ? '1' : '0');
    bt[7] = ( (c & 0x08) ? '1' : '0');
    bt[8] = NULL;

    return bt;
}
